mapper.select:
SELECT * FROM ROOM
ORDER BY
room_idx ASC;

SELECT R.ROOM_IDX, R.ROOM_NAME, R.ROOM_INFO, R.ROOM_CONDITION, R.ROOM_CATEGORY, R.PERSON_NUM, R.DOG_NUM, R.ROOM_ITEM_IDX, R.ROOM_GUIDE, R.ROOM_SIZE, R.ROOM_CHECKIN, R.ROOM_CHECKOUT, STRING_AGG(I.ROOM_ITEM_NAME, ', ') AS ROOM_ITEM_NAMES FROM ROOM R INNER JOIN ROOM_ITEM I ON POSITION(I.ROOM_ITEM_IDX IN R.ROOM_ITEM_IDX) > 0
AND R.ROOM_IDX = #{room_idx}
GROUP BY R.ROOM_IDX, R.ROOM_NAME, R.ROOM_INFO, R.ROOM_CONDITION, R.ROOM_CATEGORY, R.PERSON_NUM, R.DOG_NUM, R.ROOM_ITEM_IDX, R.ROOM_GUIDE, R.ROOM_SIZE, R.ROOM_CHECKIN, R.ROOM_CHECKOUT
